import React from 'react';

import './style.css';

export default function SectionOurStory() {

    const stream = document.querySelector('.slider__stream');
    const items = document.querySelector('.slider__item');

    const image = ['sete', 'oito', 'nove', 'vinte'];
    const i = 0;

    const prev = document.querySelector('.slider__prev');
    prev.addEventListener('click', function(stream, items) {
        if(i >= 1 && i <= image.length) {

            stream.insertBefore(items[items.length - 1], items[0]);
            this.items = document.querySelectorAll('.slider__item');

            i--;

            if (i = 0) {
                var ano = document.querySelector('.ano');
                var fundo = document.querySelector('.slider__item:nth-child(3)');

                ano.innerHTML = '2017';
                fundo.style.background = 'black';
            }
        }
    });

    const next = document.querySelector('.slider__next');
    prev.addEventListener('click', function() {
        if(i >= 0 && i <= image.length - 2) {

            stream.appendChild(items[0])
            this.items = document.querySelectorAll('.slider__item');

            i++;

            if (i = 1) {   
                var ano = document.querySelector('.ano');
                var fundo = document.querySelector('.fighter__item:nth-child(3)');

                ano.innerHTML = '2018';
                fundo.style.background = 'yellow';
                console.log(i)
            } 
        }
    });

    return (
        <section className="OurStory">

            <div className="text-story">
                <h1>Nossa <span>hist√≥ria</span></h1>
            </div>

            <h2 className="years">2017</h2>

            <div className="sliderStory">
                <div class="slider-story">
                    <div class="slider__prev" onClick={prev}></div>
                    <div class="slider__next" onClick={next}></div>
                    <div class="slider__stream">
                        <div class="slider__item bg-1"></div>
                        <div class="slider__item bg-2"></div>
                        <div class="slider__item bg-3">
                            teste
                        </div>
                        <div class="slider__item bg-4"></div>
                    </div>
                </div>
            </div>
        </section>
    )
}
